-- 将该建表 SQL 语句，添加到 yudao-module-purchase-biz 模块的 test/resources/sql/create_tables.sql 文件里
CREATE TABLE IF NOT EXISTS "purchase_order" (
    "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "purchase_type" varchar,
    "secondary_classification" varchar,
    "purchase_order_number" varchar NOT NULL,
    "provider_id" bigint NOT NULL,
    "provider_name" varchar NOT NULL,
    "provider_contact_information" varchar NOT NULL,
    "purchase_time" datetime NOT NULL,
    "purchase_completion_time" datetime,
    "total_procurement" varchar,
    "settlement_method" varchar,
    "beneficiary_name" varchar,
    "bank_of_deposit" varchar,
    "collection_account_no" varchar,
    "payment_method" varchar,
    "purchaser_id" bigint NOT NULL,
    "purchaser_name" varchar,
    "procurement_department_id" varchar,
    "procurement_department_name" varchar,
    "province" varchar,
    "city" varchar,
    "area" varchar,
    "detailed_address" varchar,
    "longitude" varchar,
    "latitude" varchar,
    "base_id" bigint,
    "massif_id" bigint,
    "purchase_status" varchar,
    "quotation_id" bigint,
    "quotation_no" varchar,
    "term_of_validity" varchar,
    "collection_batch_no" varchar,
    "transaction_no" varchar,
    "purchase_order_source" varchar,
    "status" varchar,
    "creator" varchar DEFAULT '',
    "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updater" varchar DEFAULT '',
    "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    "deleted" bit NOT NULL DEFAULT FALSE,
    PRIMARY KEY ("id")
) COMMENT '采购单';

-- 将该删表 SQL 语句，添加到 yudao-module-purchase-biz 模块的 test/resources/sql/clean.sql 文件里
DELETE FROM "purchase_order";
