-- 将该建表 SQL 语句，添加到 yudao-module-service-biz 模块的 test/resources/sql/create_tables.sql 文件里
CREATE TABLE IF NOT EXISTS "service_service_info" (
    "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "service_code" varchar,
    "service_name" varchar,
    "service_type" varchar,
    "service_provider" varchar,
    "content" varchar,
    "service_payer" varchar,
    "start_time" datetime,
    "end_time" datetime,
    "state" varchar,
    "indu_facility_info_id" varchar,
    "remarks" varchar,
    "tunnel_info_id" varchar,
    "devices_id" varchar,
    "indu_subject_info_id" varchar,
    "carrier_id" varchar,
    "children_table_name" varchar,
    "service_fee" varchar,
    "category_id" varchar,
    "available_time" varchar,
    "farm_type" varchar,
    "introduce" varchar,
    "address" varchar,
    "rotation_imgs" varchar,
    "details_imgs" varchar,
    "max_attend_people" varchar,
    "attend_people" varchar,
    "basesid" varchar,
    "basesname" varchar,
    "plant_num" varchar,
    "plant_crop_ids" varchar,
    "label" varchar,
    "collect_num" varchar,
    "status" int,
    "creator" varchar DEFAULT '',
    "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updater" varchar DEFAULT '',
    "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    "deleted" bit NOT NULL DEFAULT FALSE,
    "tenant_id" bigint NOT NULL,
    "source" varchar,
    PRIMARY KEY ("id")
) COMMENT '服务表';

-- 将该删表 SQL 语句，添加到 yudao-module-service-biz 模块的 test/resources/sql/clean.sql 文件里
DELETE FROM "service_service_info";
