mapper.select:
select scs.id , scs.specifications_name AS name, scs.packaging_type AS packagingType, scs.packaging_type_name AS packagingTypeName, scs.number, scs.unit, scs.unit_name AS unitName, scs.packaging, scs.packaging_name AS packagingName, scs.measurement_unit AS measurementUnit, scs.measurement_unit_name AS measurementUnitName, scs.purchase_price AS purchasePrice, scs.selling_price AS sellingPrice, CONCAT(scs.number,scs.unit_name,"/",scs.packaging_name) AS specifications, sc.commodity_name AS salesName, scc.commodity_category_name AS classifyName FROM sales_commodity_specification scs LEFT JOIN sales_commodity sc on scs.commodity_id = sc.id LEFT JOIN sales_commodity_category scc on scc.id = sc.commodity_category_id where scs.deleted = 0 and sc.deleted=0
AND scs.commodity_id=#{salesId}
AND sd.commodity_name like CONCAT('%',#{salesName},'%')

select scs.id , scs.specifications_name AS name, scs.packaging_type AS packagingType, scs.packaging_type_name AS packagingTypeName, scs.number, scs.unit, scs.unit_name AS unitName, scs.packaging, scs.packaging_name AS packagingName, scs.measurement_unit AS measurementUnit, scs.measurement_unit_name AS measurementUnitName, scs.purchase_price AS purchasePrice, scs.selling_price AS sellingPrice, CONCAT(scs.number,scs.unit_name,"/",scs.packaging_name) AS specifications, sc.commodity_name AS salesName, scc.commodity_category_name AS classifyName FROM sales_commodity_specification scs LEFT JOIN sales_commodity sc on scs.commodity_id = sc.id LEFT JOIN sales_commodity_category scc on scc.id = sc.commodity_category_id where scs.deleted = 0
AND scs.commodity_id=#{salesId}
AND sd.commodity_name like CONCAT('%',#{salesName},'%')
LIMIT #{pageNo} ,#{pageSize}

select count(1) FROM sales_commodity_specification scs where scs.deleted = 0
AND scs.commodity_id=#{salesId}
AND sd.commodity_name like CONCAT('%',#{salesName},'%')
