mapper.select:
select id , name as name FROM sales_special_medical_food where deleted = 0
AND name like CONCAT('%',#{salesName},'%')
AND id =#{salesId}

select ssmfs.id , ssmfs.specifications_name AS name, ssmfs.packaging_type AS packagingType, ssmfs.packaging_type_name AS packagingTypeName, ssmfs.number, ssmfs.unit, ssmfs.unit_name AS unitName, ssmfs.packaging, ssmfs.packaging_name AS packagingName, ssmfs.measurement_unit AS measurementUnit, ssmfs.measurement_unit_name AS measurementUnitName, ssmfs.purchase_price AS purchasePrice, ssmfs.selling_price AS sellingPrice, CONCAT(ssmfs.number,ssmfs.unit_name,"/",ssmfs.packaging_name) AS specifications, ssmf.name AS salesName, ssmfc.category_name AS classifyName FROM sales_special_medical_food_specification ssmfs LEFT JOIN sales_special_medical_food ssmf on ssmfs.food_id = ssmf.id LEFT JOIN sales_special_medical_food_classify ssmfc on ssmfc.id = ssmf.classify where ssmfs.deleted = 0 and ssmf.deleted=0
AND ssmfs.food_id=#{salesId}
AND ssmf.name like CONCAT('%',#{salesName},'%')

select ssmfs.id , ssmfs.specifications_name AS name, ssmfs.packaging_type AS packagingType, ssmfs.packaging_type_name AS packagingTypeName, ssmfs.number, ssmfs.unit, ssmfs.unit_name AS unitName, ssmfs.packaging, ssmfs.packaging_name AS packagingName, ssmfs.measurement_unit AS measurementUnit, ssmfs.measurement_unit_name AS measurementUnitName, ssmfs.purchase_price AS purchasePrice, ssmfs.selling_price AS sellingPrice, CONCAT(ssmfs.number,ssmfs.unit_name,"/",ssmfs.packaging_name) AS specifications, ssmf.name AS salesName, ssmfc.category_name AS classifyName FROM sales_special_medical_food_specification ssmfs LEFT JOIN sales_special_medical_food ssmf on ssmfs.food_id = ssmf.id LEFT JOIN sales_special_medical_food_classify ssmfc on ssmfc.id = ssmf.classify where ssmfs.deleted = 0
AND ssmfs.food_id=#{salesId}
AND ssmf.name like CONCAT('%',#{salesName},'%')
AND ssmfs.id =#{specificationsId}
LIMIT #{pageNo} ,#{pageSize}

select count(1) FROM sales_special_medical_food_specification ssmfs where ssmfs.deleted = 0
AND ssmfs.food_id=#{salesId}
AND ssmf.name like CONCAT('%',#{salesName},'%')
AND ssmfs.id =#{specificationsId}
