mapper.select:
select id , specifications_name AS name, packaging_type AS packagingType, packaging_type_name AS packagingTypeName, number, unit, unit_name AS unitName, packaging, packaging_name AS packagingName, measurement_unit AS measurementUnit, measurement_unit_name AS measurementUnitName, purchase_price AS purchasePrice, selling_price AS sellingPrice, CONCAT(number,unit_name,"/",packaging_name) AS specifications FROM shelves_specifications where deleted = 0
AND shelves_id=#{shelvesId}
AND tenant_id =#{tenantId}
AND id =#{specificationsId}
