mapper.select:
SELECT ssr.id, ss.specifications_name as specificationsName, ssr.type, ssr.amount, ssr.create_time as createTime, ssr.creator, ssr.update_time as updateTime from shelves_stock_record ssr LEFT JOIN shelves_specifications ss ON ssr.specifications_id = ss.id where ssr.deleted = 0
AND ss.specifications_name like CONCAT('%',#{specificationsName},'%')
AND ssr.type = #{type}
AND ssr.creator like CONCAT('%',#{creator},'%')
AND ssr.update_time BETWEEN
#{time}
AND ssr.type = #{type}
LIMIT #{pageNo} ,#{pageSize}

SELECT count(1) from shelves_stock_record ssr LEFT JOIN shelves_specifications ss ON ssr.specifications_id = ss.id where ssr.deleted = 0
AND ss.specifications_name like CONCAT('%',#{specificationsName},'%')
AND ssr.creator like CONCAT('%',#{creator},'%')
AND ssr.type = #{type}
AND ssr.update_time BETWEEN
#{time}
AND ssr.type = #{type}
